import { Component } from 'react';
// import ReactDOM from 'react-dom';
import Player from "./Player";
import './App.css';


const weapons = ["rock", "paper", "scissors"];

class App extends Component {
  state = {
    player: weapons[0],
    bot: weapons[0],
    winner: ""
  };

  startGame = () => {
    let cpt = 0;
    let gameInterval = setInterval(() => {
      cpt++;

      this.setState({
        bot: weapons[Math.floor(Math.random() * weapons.length)],
        winner: ""
      });

      if (cpt > 5) {
        clearInterval(gameInterval);
        this.setState({
          winner: this.selectWinner()
        });
      }
    }, 100);
  }
  selectWinner = () => {
    const { player, bot } = this.state;

    if (player === bot) {
      return "Oops it's a Tie!";
    } else if ((player === "rock" && bot === "scissors") ||
      (player === "scissors" && bot === "paper") ||
      (player === "paper" && bot === "rock")) {
      return "Player Wins!";
    } else {
      return "Bot Wins!";
    }
  };

  selectWeapon = weapon => {
    this.setState({
      player: weapon,
      winner: ""
    });
  };

  render() {
    const { player, bot, winner } = this.state;
    return (
      <>
        <h1 className="title">Rock Paper Scissors</h1>

        <div>
          <Player weapon={player} />
          <Player weapon={bot} />
        </div>

        <section className="game">
          <div className="player-score">
            <h2>Player</h2>
            <p>0</p>
          </div>
          <div className="bot-score">
            <h2>Bot</h2>
            <p>0</p>
          </div>
        </section>

        <div className="intro">
          <h1>Rock Paper and Scissors</h1>
          <button></button>
        </div>

        <div>
          <button className="weaponBtn" onClick={() => this.selectWeapon("rock")}>
            rock
          </button>
          <button className="weaponBtn" onClick={() => this.selectWeapon("paper")}>
            paper
          </button>
          <button className="weaponBtn" onClick={() => this.selectWeapon("scissors")}>
            scissor
          </button>
        </div>

        <div className="winner">{winner ? this.selectWinner() : null}</div>
        <button type="button" onClick={this.startGame}>
          Start!
        </button>
      </>
    );
  }
}

export default App;
