import { Component } from 'react';
import ReactDOM from 'react-dom';
import './App.css';


const weapons = ["rock", "paper", "scissors"];

class App extends Component {
  state = {
    player: weapons[0],
    bot: weapons[0],
    winner: ""
  };

  Game = () => {
    let cpt = 0;
    let gameInterval = setInterval (() => {
      cpt++;

      this.setState({
        bot: weapons[Math.floor(Math.random() * weapons.length)],
        winner: ""
      });

      if (cpt > 5) {
        clearInterval(gameInterval);
        this.setState({
          winner: this.selectWinner()
        });
      }
    }, 100);
  }
  selectWinner = () => {
    const { playerOne, bot } = this.state;

    if (playerOne === bot) {
      return "Oops it's a Tie!";
    } else if (
      (playerOne === "rock" && bot === "scissors") ||
      (playerOne === "scissors" && bot === "paper") ||
      (playerOne === "paper" && bot === "rock")
    ) {
      return "Player One Wins!";
    } else {
      return "Player Two Wins!";
    }
  };

  selectWeapon = weapon => {
    this.setState({
      playerOne: weapon,
      winner: ""
    });
  };
  render() {
    const { playerOne, bot, winner } = this.state;
    return (
      <>
        <h1 style={{ textAlign: "center" }}>Rock Paper Scissors</h1>

        <div>
          <Player weapon={playerOne} />
          <Player weapon={bot} />
        </div>
        <div>
          <button
            className="weaponBtn"
            onClick={() => this.selectWeapon("rock")}
          >
            rock
          </button>
          <button
            className="weaponBtn"
            onClick={() => this.selectWeapon("paper")}
          >
            paper
          </button>
          <button
            className="weaponBtn"
            onClick={() => this.selectWeapon("scissors")}
          >
            scissor
          </button>
        </div>
        <div className="winner">{winner ? this.selectWinner() : null}</div>
        <button type="button" onClick={this.startGame}>
          Start!
        </button>
      </>
    );
  }
}

export default App;
